using System.Text.Json.Nodes;
using ContosoAds.Web.DataAccess;
using Microsoft.Extensions.DependencyInjection;

namespace ContosoAds.Web.Tests;

internal static class TestSupport
{
    internal static async Task<AdsContext> CreateAdsContext(
        string connectionString, bool recreate = false,
        params IReadOnlyList<Ad> ads)
    {
        var options = new DbContextOptionsBuilder<AdsContext>()
            .UseNpgsql(connectionString)
            .Options;
        var context = new AdsContext(options);
        await context.Database.MigrateAsync();

        if (recreate)
        {
            await context.Ads.ExecuteDeleteAsync(TestContext.Current.CancellationToken);
        }

        await SeedAdsContextAsync(context, ads);
        return context;
    }

    internal static async Task<IEnumerable<int>> SeedDatabaseAsync(
        IServiceScope scope,
        params IReadOnlyList<Ad> ads)
    {
        var context = scope.ServiceProvider.GetRequiredService<AdsContext>();
        await context.Database.MigrateAsync(TestContext.Current.CancellationToken);
        await context.Ads.ExecuteDeleteAsync(TestContext.Current.CancellationToken);
        return await SeedAdsContextAsync(context, ads);
    }

    private static async Task<IEnumerable<int>> SeedAdsContextAsync(
        AdsContext context,
        params IReadOnlyList<Ad> ads)
    {
        if (ads is { Count: 0 })
        {
            return [];
        }

        await context.Ads.AddRangeAsync(ads);
        await context.SaveChangesAsync();
        return ads.Select(a => a.Id);
    }

    internal static JsonNode CreateBlobResponse(string fileName) =>
        JsonNode.Parse($"{{\"blobURL\":\"https://www.example.com/{fileName}\"}}")!;

    internal const string Jpeg =
        "";

    internal static readonly byte[] JpegBytes = Convert.FromBase64String(Jpeg);
}